
// module
module.expo = {
    // esto es un modulo que va  aexportar funciones

    sumar: function (a,b){
        return a + b;
    },
    restar: function (a,b) {
        return a-b;

    },
    multiplicacion: function(a,b){
        return a * b;
    },
    dividir: function(a,b){
        return a / b;
    }

}

module.exports = {
    sumar,
    restar,
    multiplicacion,
    dividir
}